function typewriter(t,e){let n=0;t.textContent="",t.style.visibility="visible";const i=setInterval((()=>{n<e.length?(t.textContent+=e.charAt(n),n++):clearInterval(i)}),50)}document.addEventListener("DOMContentLoaded",(function(){const t=document.querySelectorAll("#title, #intro,  #intro-1,  #intro-2, #fact1, #fact2, #fact3, #main, #title2, #links a");let e=0;t.forEach((t=>{setTimeout((()=>{typewriter(t,t.textContent)}),e),e+=50*t.textContent.length+1e3}))}));